import React, { useState, useEffect } from "react";

function RSVP() {
  const [code, setCode] = useState("");
  const [party, setParty] = useState(null);
  const [isLoading, setIsLoading] = useState(false);
  const [showSuccess, setShowSuccess] = useState(false);
  const [successMessage, setSuccessMessage] = useState("");

  useEffect(() => {
    const fetchPartyData = async () => {
      try {
        const response = await fetch("http://localhost:5001/partyData");
        if (response.ok) {
          const data = await response.json();
          setParty(data);
        } else {
          console.error("Error fetching party data");
          // Handle error (e.g., display an error message to the user)
        }
      } catch (error) {
        console.error("Error fetching party data:", error);
        // Handle error (e.g., display an error message to the user)
      }
    };

    fetchPartyData();
  }, []);

  const handleCodeChange = (event) => {
    setCode(event.target.value);
  };

  const handleSubmit = () => {
    const foundParty = party[code];
    if (foundParty) {
      setParty(foundParty); // Set party to the foundParty
    } else {
      alert("Invalid code. Please try again.");
    }
  };

  const handleRSVP = async (memberId, newRsvpStatus) => {
    setIsLoading(true);
    try {
      const response = await fetch("http://localhost:5001/rsvp", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ code, memberId, rsvpStatus: newRsvpStatus }),
      });

      if (response.ok) {
        setShowSuccess(true);
        setSuccessMessage("RSVP submitted successfully!");

        // Update RSVP status in the local state
        setParty((prevParty) => ({
          ...prevParty,
          members: prevParty.members.map((member) =>
            member.name === memberId
              ? { ...member, rsvp: newRsvpStatus }
              : member,
          ),
        }));
      } else {
        const data = await response.json();
        console.error("RSVP failed:", data.error);
        alert("RSVP failed. Please try again later.");
      }
    } catch (error) {
      console.error("Error sending RSVP:", error);
      alert("An error occurred. Please try again later.");
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div>
      <h1>RSVP</h1>
      <input
        type="text"
        value={code}
        onChange={handleCodeChange}
        placeholder="Enter your party code"
      />
      <button onClick={handleSubmit}>Submit Code</button>

      {party ? (
        <div>
          <p>Number of Guests: {party.guests}</p>
          <p>{party.note}</p>
          {party.photos &&
            party.photos.map((photo, index) => (
              <img
                key={index}
                src={photo}
                alt={`Wedding photo ${index + 1}`}
                style={{ width: "200px", height: "auto" }}
              />
            ))}

          <h2>Guests</h2>
          <ul>
            {party.members.map((member) => (
              <li key={member.name}>
                {member.name} - Allergies: {member.allergies} -
                <span>
                  <button onClick={() => handleRSVP(member.name, "yes")}>
                    Yes
                  </button>
                  <button onClick={() => handleRSVP(member.name, "no")}>
                    No
                  </button>
                  {showSuccess && (
                    <div className="success-message">{successMessage}</div>
                  )}
                </span>
              </li>
            ))}
          </ul>
        </div>
      ) : (
        <div>Loading party data...</div>
      )}
    </div>
  );
}

export default RSVP;
