import React, { useEffect, useState } from 'react';
import './PartyTable.css';

const PartyTable = () => {
    const [partyData, setPartyData] = useState({});
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    useEffect(() => {
        const fetchPartyData = async () => {
            try {
                const response = await fetch("http://localhost:5001/partyData");
                if (response.ok) {
                    const data = await response.json();
                    setPartyData(data);
                } else {
                    setError("Failed to fetch party data.");
                }
            } catch (error) {
                setError("Error fetching party data.");
                console.error(error);
            } finally {
                setLoading(false);
            }
        };

        fetchPartyData();
    }, []);

    if (loading) return <p>Loading...</p>;
    if (error) return <p>{error}</p>;

    return (
        <div className="party-table-container">
            <h1>RSVP Status</h1>
            <table className="party-table">
                <thead>
                    <tr>
                        <th>Party Name</th>
                        <th>Guest Name</th>
                        <th>RSVP</th>
                        <th>Allergies</th>
                    </tr>
                </thead>
                <tbody>
                    {Object.entries(partyData).map(([partyName, details]) =>
                        details.members.map((member, index) => (
                            <tr key={`${partyName}-${index}`}>
                                <td>{partyName}</td>
                                <td>{`${member.firstname} ${member.lastname}`}</td>
                                <td>{member.rsvp || 'No response'}</td>
                                <td>{member.allergies || 'None'}</td>
                            </tr>
                        ))
                    )}
                </tbody>
            </table>
        </div>
    );
};

export default PartyTable;
