import React, { useState } from "react";

// Replace with your actual guest data (make sure it's valid JSON)
const guestData = {
  GUESTCODE1: {
    name: "John Doe",
    guests: 2,
    allergies: "None",
    note: "So glad you can make it!",
    photos: ["/photo1.jpg", "/photo2.jpg"],
  },
  GUESTCODE2: {
    name: "Jane Smith",
    guests: 1,
    allergies: "Peanuts",
    note: "Looking forward to celebrating with you!",
    photos: ["/photo3.jpg"],
  },
  // ... more guest data
};

function RSVP() {
  const [code, setCode] = useState("");
  const [guest, setGuest] = useState(null);
  const [rsvpStatus, setRsvpStatus] = useState("");
  const [isLoading, setIsLoading] = useState(false);
  const [showSuccess, setShowSuccess] = useState(false);
  const [successMessage, setSuccessMessage] = useState("");
  const [allergies, setAllergies] = useState("");

  const handleCodeChange = (event) => {
    setCode(event.target.value);
  };

  const handleSubmit = () => {
    const foundGuest = guestData[code];
    if (foundGuest) {
      setGuest(foundGuest);
      setAllergies(foundGuest.allergies); // Pre-fill allergies
    } else {
      alert("Invalid code. Please try again.");
    }
  };

  const handleRSVP = async () => {
    setIsLoading(true);
    try {
      const response = await fetch("http://localhost:5001/rsvp", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ code, rsvpStatus, allergies }), // Include allergies in request
      });

      if (response.ok) {
        setShowSuccess(true);
        if (rsvpStatus === "attending") {
          setSuccessMessage("Yay! We can't wait to celebrate with you!");
        } else {
          setSuccessMessage("We'll miss you, but thanks for letting us know!");
        }
        // Optional: Hide the message after a few seconds
        // setTimeout(() => setShowSuccess(false), 3000);
      } else {
        const data = await response.json();
        console.error("RSVP failed:", data.error);
        alert("RSVP failed. Please try again later.");
      }
    } catch (error) {
      console.error("Error sending RSVP:", error);
      alert("An error occurred. Please try again later.");
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div>
      <h1>RSVP</h1>
      <input
        type="text"
        value={code}
        onChange={handleCodeChange}
        placeholder="Enter your code"
      />
      <button onClick={handleSubmit}>Submit Code</button>

      {guest && (
        <div>
          <p>Name: {guest.name}</p>
          <p>Number of Guests: {guest.guests}</p>
          <div>
            <label htmlFor="allergies">Allergies:</label>
            <input
              type="text"
              id="allergies"
              value={allergies}
              onChange={(e) => setAllergies(e.target.value)}
            />
          </div>
          <p>{guest.note}</p>
          {guest.photos.map((photo, index) => (
            <img
              key={index}
              src={photo}
              alt={`Wedding photo ${index + 1}`}
              style={{ width: "200px", height: "auto" }}
            />
          ))}

          <div>
            <label>
              <input
                type="radio"
                value="attending"
                checked={rsvpStatus === "attending"}
                onChange={() => setRsvpStatus("attending")}
              />
              Attending
            </label>
            <label>
              <input
                type="radio"
                value="not attending"
                checked={rsvpStatus === "not attending"}
                onChange={() => setRsvpStatus("not attending")}
              />
              Not Attending
            </label>
          </div>

          <button onClick={handleRSVP} disabled={isLoading}>
            {isLoading ? "Submitting..." : "Submit RSVP"}
          </button>

          {showSuccess && (
            <div className="success-message">{successMessage}</div>
          )}
        </div>
      )}
    </div>
  );
}

export default RSVP;
