import React, { useState, useEffect } from "react";

function RSVP() {
  const [code, setCode] = useState("");
  const [party, setParty] = useState(null);
  const [isLoading, setIsLoading] = useState(false);
  const [showSuccess, setShowSuccess] = useState(false);
  const [successMessage, setSuccessMessage] = useState("");

  // State to track RSVP status for each member
  const [memberRSVPs, setMemberRSVPs] = useState({});

  useEffect(() => {
    const fetchPartyData = async () => {
      try {
        const response = await fetch("http://localhost:5001/partyData");
        if (response.ok) {
          const data = await response.json();
          setParty(data);
        } else {
          console.error("Error fetching party data");
        }
      } catch (error) {
        console.error("Error fetching party data:", error);
      }
    };

    fetchPartyData();
  }, []);

  const handleCodeChange = (event) => {
    setCode(event.target.value);
  };

  const handleSubmit = () => {
    const foundParty = party[code];
    if (foundParty) {
      setParty(foundParty);

      // Initialize member RSVPs
      const initialRSVPs = {};
      foundParty.members.forEach((member) => {
        initialRSVPs[member.name] = "no"; // Default to 'no'
      });
      setMemberRSVPs(initialRSVPs);
    } else {
      alert("Invalid code. Please try again.");
    }
  };

  const handleRSVPChange = (memberId, newRsvpStatus) => {
    setMemberRSVPs((prevRSVPs) => ({
      ...prevRSVPs,
      [memberId]: newRsvpStatus,
    }));
  };

  const handleRSVP = async () => {
    setIsLoading(true);
    try {
      const response = await fetch("http://localhost:5001/rsvp", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ code, memberRSVPs }), // Send all RSVPs
      });

      if (response.ok) {
        setShowSuccess(true);
        setSuccessMessage("RSVP submitted successfully!");

        // Update RSVP status in the local state
        setParty((prevParty) => ({
          ...prevParty,
          members: prevParty.members.map((member) => ({
            ...member,
            rsvp: memberRSVPs[member.name],
          })),
        }));
      } else {
        const data = await response.json();
        console.error("RSVP failed:", data.error);
        alert("RSVP failed. Please try again later.");
      }
    } catch (error) {
      console.error("Error sending RSVP:", error);
      alert("An error occurred. Please try again later.");
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div>
      <h1>RSVP</h1>
      <input
        type="text"
        value={code}
        onChange={handleCodeChange}
        placeholder="Enter your party code"
      />
      <button onClick={handleSubmit}>Submit Code</button>

      {party ? (
        <div>
          <p>Number of Guests: {party.guests}</p>
          <p>{party.note}</p>
          {party.photos &&
            party.photos.map((photo, index) => (
              <img
                key={index}
                src={photo}
                alt={`Wedding photo ${index + 1}`}
                style={{ width: "200px", height: "auto" }}
              />
            ))}

          <h2>Guests</h2>
          <ul>
            {party.members &&
              party.members.map((member) => (
                <li key={member.name}>
                  {member.name} - Allergies: {member.allergies} -
                  <span>
                    <label>
                      <input
                        type="radio"
                        value="yes"
                        checked={memberRSVPs[member.name] === "yes"}
                        onChange={() =>
                          handleRSVPChange(member.name, "yes")
                        }
                      />
                      Yes
                    </label>
                    <label>
                      <input
                        type="radio"
                        value="no"
                        checked={memberRSVPs[member.name] === "no"}
                        onChange={() => handleRSVPChange(member.name, "no")}
                      />
                      No
                    </label>
                  </span>
                </li>
              ))}
          </ul>

          <button onClick={handleRSVP} disabled={isLoading}>
            {isLoading ? "Submitting..." : "Submit RSVP"}
          </button>
          {showSuccess && (
            <div className="success-message">{successMessage}</div>
          )}
        </div>
      ) : (
        <div>Loading party data...</div>
      )}
    </div>
  );
}

export default RSVP;
