import React, { useState } from 'react';
import { BrowserRouter, Routes, Route } from 'react-router-dom';
import HomePage from './HomePage';
import RSVP from './RSVP';
import FAQ from './FAQ';
import Navbar from './Navbar'; // Import Navbar

function App() {
  // Handle partyCode globally in App.js
  const [inputPartyCode, setInputPartyCode] = useState(""); // Global state for party code

  // Function to reset the party code
  const resetPartyCode = () => {
    setInputPartyCode(""); // Reset the party code globally
  };

  return (
    <BrowserRouter>
      {/* Navbar across all routes */}
      <Navbar resetPartyCode={resetPartyCode} /> 

      <Routes>
        <Route path="/" element={<HomePage />} />
        {/* Pass the inputPartyCode and resetPartyCode as props to RSVP */}
        <Route path="/rsvp" element={<RSVP inputPartyCode={inputPartyCode} setInputPartyCode={setInputPartyCode} />} />
        <Route path="/rsvp/:partyCode" element={<RSVP inputPartyCode={inputPartyCode} setInputPartyCode={setInputPartyCode} />} />
        <Route path="/faq" element={<FAQ />} />
      </Routes>
    </BrowserRouter>
  );
}

export default App;
