import React, { useState, useEffect } from "react";

function RSVP() {
  // ... (state) ...

  useEffect(() => {
    // ... (fetch party data) ...
  }, []);

  // ... (handleCodeChange, handleSubmit, handleRSVPChange, handleAllergyChange, handleRSVP) ...

  return (
    <div>
      <h1>RSVP</h1>
      <input
        type="text"
        value={code}
        onChange={handleCodeChange}
        placeholder="Enter your party code"
      />
      <button onClick={handleSubmit}>Submit Code</button>

      {party ? (
        <div>
          <p>Number of Guests: {party.guests}</p>
          <p>{party.note}</p>
          {party.photos &&
            party.photos.map((photo, index) => (
              <img
                key={index}
                src={photo}
                alt={`Wedding photo ${index + 1}`}
                style={{ width: "200px", height: "auto" }}
              />
            ))}

          <h2>Guests</h2>
          <ul>
            {party.members &&
              party.members.map((member) => (
                <li key={member.name}>
                  {member.name} -
                  <span>
                    <label htmlFor={`${member.name}-allergies`}>
                      Allergies:
                    </label>
                    <input
                      type="text"
                      id={`${member.name}-allergies`}
                      value={memberAllergies[member.name]}
                      onChange={(e) =>
                        handleAllergyChange(member.name, e.target.value)
                      }
                    />
                  </span>
                  -
                  <span>
                    <label>
                      <input
                        type="radio"
                        value="yes"
                        checked={memberRSVPs[member.name] === "yes"}
                        onChange={() => handleRSVPChange(member.name, "yes")}
                      />
                      Yes
                    </label>
                    <label>
                      <input
                        type="radio"
                        value="no"
                        checked={memberRSVPs[member.name] === "no"}
                        onChange={() => handleRSVPChange(member.name, "no")}
                      />
                      No
                    </label>
                  </span>
                </li>
              ))}
          </ul>

          <button onClick={handleRSVP} disabled={isLoading}>
            {isLoading ? "Submitting..." : "Submit RSVP"}
          </button>
          {showSuccess && (
            <div className="success-message">{successMessage}</div>
          )}
        </div>
      ) : (
        <div>Loading party data...</div>
      )}
    </div>
  );
}

export default RSVP;
