import React, { useState } from 'react';

// Replace with your actual guest data (make sure it's valid JSON)
const guestData = {
  "GUESTCODE1": {
    "name": "John Doe",
    "guests": 2,
    "allergies": "None",
    "note": "So glad you can make it!",
    "photos": ["/photo1.jpg", "/photo2.jpg"] // Update with actual image paths
  },
  // ... more guest data
};

function RSVP() {
  const [code, setCode] = useState('');
  const [guest, setGuest] = useState(null);
  const [rsvpStatus, setRsvpStatus] = useState(''); // Add state for RSVP status
  const [isLoading, setIsLoading] = useState(false); // Add state for loading

  const handleCodeChange = (event) => {
    setCode(event.target.value);
  };

  const handleSubmit = () => {
    const foundGuest = guestData[code];
    if (foundGuest) {
      setGuest(foundGuest);
    } else {
      // Handle invalid code (e.g., show an error message)
      alert('Invalid code. Please try again.');
    }
  };

  const handleRSVP = async () => { 
    setIsLoading(true); // Set loading state
    try {
      const response = await fetch('/rsvp', {  // Use the proxy or full URL
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ code, rsvpStatus })
      });

      if (response.ok) {
        // RSVP successful
        alert('RSVP submitted successfully!'); 
      } else {
        const data = await response.json();
        // Handle error (e.g., display the error message from the server)
        console.error('RSVP failed:', data.error);
        alert('RSVP failed. Please try again later.'); 
      }
    } catch (error) {
      console.error('Error sending RSVP:', error);
      alert('An error occurred. Please try again later.');
    } finally {
      setIsLoading(false); // Reset loading state
    }
  };

  return (
    <div>
      <h1>RSVP</h1>
      <input
        type="text"
        value={code}
        onChange={handleCodeChange}
        placeholder="Enter your code"
      />
      <button onClick={handleSubmit}>Submit Code</button>

      {guest && (
        <div>
          <p>Name: {guest.name}</p>
          <p>Number of Guests: {guest.guests}</p>
          <p>Allergies: {guest.allergies}</p>
          <p>{guest.note}</p>
          {/* Display photos */}
          {guest.photos.map((photo, index) => (
            <img
              key={index}
              src={photo}
              alt={`Wedding photo ${index + 1}`}
              style={{ width: '200px', height: 'auto' }} // Add some styling
            />
          ))}

          {/* RSVP options */}
          <div>
            <label>
              <input
                type="radio"
                value="attending"
                checked={rsvpStatus === 'attending'}
                onChange={() => setRsvpStatus('attending')}
              />
              Attending
            </label>
            <label>
              <input
                type="radio"
                value="not attending"
                checked={rsvpStatus === 'not attending'}
                onChange={() => setRsvpStatus('not attending')}
              />
              Not Attending
            </label>
          </div>

          <button onClick={handleRSVP} disabled={isLoading}>
            {isLoading ? 'Submitting...' : 'Submit RSVP'}
          </button>
        </div>
      )}
    </div>
  );
}

export default RSVP;
